import React, { useState, useEffect } from 'react'
import axios from 'axios'
const Forum = () => {
    const [messages, setMessages] = useState([])
    const [message, setMessage] = useState()
    const [counter, setCounter] = useState(0)

    const [messageName, setMessageName] = useState('messagebubble')
    const name = window.localStorage.getItem('name')
    const u = window.localStorage.getItem('userInfo')
    const token = window.localStorage.getItem('token')
    window.setInterval(function () {
        setCounter(counter + 1)
    }, 2000)
    console.log(counter)
    useEffect(() => {
        axios
            .get('https://jobs-xmmtw.ondigitalocean.app/Forum/messaging', {
                headers: {
                    authorization: token,
                },
            })
            .then((res) => {
                console.log(res)
                setMessages(res.data)
            })
    }, [])

    const Submit = (e) => {
        e.preventDefault()
        axios
            .post(
                'https://jobs-xmmtw.ondigitalocean.app/Forum/messaging',
                {
                    content: message,
                    name: name,
                    date: Date.now(),
                },
                {
                    headers: {
                        authorization: token,
                    },
                },
                {}
            )
            .then((res) => {
                console.log(res)
            })
            .catch((err) => {
                alert(err)
            })
    }
    const Change = (e) => {
        setMessage(e.target.value)
    }
    console.log(name)

    useEffect(() => {
        messages.map((item) => {
            if (item.name === name) {
                setMessageName('messagebubble me')
            }
        })
    }, [])

    return (
        <div className="messageCont">
            <div className="Messages">
                {messages.map((item) => {
                    if (item.name === name) {
                        return (
                            <div className="mesCont">
                                <div className="message" key={item.id}>
                                    <p className="messagebubble me">
                                        {item.content}
                                    </p>
                                    <p className="name">{item.name}</p>
                                </div>
                            </div>
                        )
                    } else {
                        return (
                            <div className="message" key={item.id}>
                                <p className="messagebubble">{item.content}</p>
                                <p className="name">{item.name}</p>
                            </div>
                        )
                    }
                })}
                <form onSubmit={Submit}>
                    <input onChange={Change} value={message} />
                    <button onClick={Submit}>Send</button>
                </form>
            </div>
        </div>
    )
}

export default Forum
