import './App.css';
import {BrowserRouter,Route,withRouter,Link} from 'react-router-dom';
import PlayerCard from './Components/PlayerCard'
import {browserHistory} from "react-router"
import logo from './images/Logo.png'
import Login from './Auth/Login'
import Signup from './Auth/Signup'
import {useState,useEffect}from 'react'
import myPlayer from './Components/myPlayer'
import axios from 'axios'
import CreatePlayer from './Components/CreatePlayer'
import { axiosWithAuth } from './Components/axosWithAuth';
import Home from './Components/Home'
import {Provider} from 'react-redux'
import {createStore,applyMiddleware} from 'redux'
import {connect} from 'react-redux'
import PrivateRoute from './PrivateRoute'
import {getId} from './actions/postActions'

const App = (props)=> {
  const token = window.localStorage.getItem('token')
   const [user,setUser]=useState([])
   useEffect(()=>{

    props.getId();

   },[])


   const Cred = {
    Status:"online"
   }

   const CredOff = {
    Status:'offline'
   }
   if(token!== ''){
  axiosWithAuth().patch(`/player/{ _id: ${props.One._id}, __v: 0 }`,Cred)
  .then(res=>{
    console.log('patched')
  }) 
}






const Logout =  () =>{
  axiosWithAuth().patch(`/player/{ _id: ${props.One._id}, __v: 0 }`,CredOff)
  .then(window.localStorage.setItem("token",''))  

}
  return (
<div className="App">

<BrowserRouter>

<nav>
  <div className = 'navCont'>
    <div className = 'logo-cont'>
  <img src = {logo} className= 'logo'/> <h1> Team Finder</h1>
    </div>
  <a href = '/'> Home </a>
  {/* <Link to = {( `/myPlayer/${props.One._id}`)}>My Player</Link> */}
  <a href = {( `/myPlayer/${props.One._id}`)} > My Player</a>
       <a href = '/login' onClick={Logout}> Logout </a>

   </div>

</nav>

  <PrivateRoute path = '/find'  exact component = {PlayerCard} />
  <Route path = '/' exact component = {Home} />
  <Route path = '/Login' exact component = {Login} />
  <Route path = '/Signup' exact component = {Signup} />
  <PrivateRoute path = '/myPlayer/:id' exact component = {myPlayer} />
     


  <Route path = 'create'  component = {CreatePlayer}/>


</BrowserRouter>

    </div>

  );
}
// const mapStateToProps =  state=>({
//     One:state.getId.UserId
// })

const mapStateToProps =  state=>({
    One:state.getId.UserId
})

export default withRouter(connect(mapStateToProps,{getId})(App))
