import React from 'react'
import {gsap, TweenMax, TimelineMax, Power3} from 'gsap/dist/gsap';
import {useState, useEffect}  from 'react'
const Animation = () => {
      let tl = new TimelineMax ();

    const heroimg = document.querySelector ('.hero-img-cont');
    const herowords = document.querySelector ('.h1-hero');
    const welcome = document.querySelector ('.welcome');
    const navi = document.querySelector ('.navi');

    tl
      .from (heroimg, 4, {scale: 1.5, ease: Power3.easeOut})
      .fromTo (navi, 1, {x: -900}, {x: 50, ease: Power3.easeOut}, '-=4')
      .from (herowords, 1, {y: 1000, ease: Power3.easeOut}, '-=4')
      .from (welcome, 1.2, {y: 1000, ease: Power3.easeOut}, '-=4');
    return (
        <div className="Animation">
        
        </div>
    )
}

export default Animation
