import React, { useState, useEffect } from 'react'
import axios from 'axios'
const Forum = () => {
    const [messages, setMessages] = useState([])
    const [message, setMessage] = useState()
    const [messageName, setMessageName] = useState('messagebubble')
    const name = window.localStorage.getItem('name')

    const token = window.localStorage.getItem('token')
    axios
        .get('https://jobs-xmmtw.ondigitalocean.app/Forum/messaging', {
            headers: {
                authorization: token,
            },
        })
        .then((res) => {
            console.log(res)
            setMessages(res.data)
        })
    const Submit = (e) => {
        e.preventDefault()
        axios
            .post(
                'https://jobs-xmmtw.ondigitalocean.app/Forum/messaging',
                {
                    content: message,
                    name: name,
                    date: Date.now(),
                },
                {
                    headers: {
                        authorization: token,
                    },
                },
                {}
            )
            .then((res) => {
                console.log(res)
            })
    }
    const Change = (e) => {
        setMessage(e.target.value)
    }
    console.log(name)

    useEffect(() => {
        messages.map((item) => {
            console.log(item.name)

            if (item.name === name) {
                setMessageName('messagebubble me')
            }
        })
    })

    return (
        <div className="messageCont">
            <div className="Messages">
                {messages.map((item) => {
                    return (
                        <div className="message" key={item.id}>
                            <p className={messageName}>{item.content}</p>
                            <p className="name">{item.name}</p>
                        </div>
                    )
                })}
                <form onSubmit={Submit}>
                    <input onChange={Change} value={message} />
                    <button onClick={Submit}>Send</button>
                </form>
            </div>
        </div>
    )
}

export default Forum
