import { POST_ID, GET_ID, GETMY_ID } from '../actions/types'
import axios from 'axios'
const token = window.localStorage.getItem('token')
const initialState = {
    UserId: [],
    UserInfo: [],
}

console.log(initialState)
export default function (state = initialState, action) {
    switch (action.type) {
        case GET_ID:
            return {
                ...state,
                UserId: action.payload,
            }

        case GETMY_ID:
            return axios
                .get(
                    `https://jobs-xmmtw.ondigitalocean.app/player/{ _id: ${initialState.UserId._id}, __v: 0 }`,
                    {
                        headers: {
                            authorization: token,
                        },
                    }
                )
                .then((res) => {
                    initialState.UserInfo = res.data
                })

        default:
            return state
    }
}
