import React, { useState, useEffect } from 'react'
import axios from 'axios'
const Forum = () => {
    const [message, setMessage] = useState()

    const [messageName, setMessageName] = useState('messagebubble')
    const name = window.localStorage.getItem('name')
    const u = window.localStorage.getItem('userInfo')
    const token = window.localStorage.getItem('token')

    useEffect(async () => {
        try {
            const name = await axios.get(
                'https://jobs-xmmtw.ondigitalocean.app/Forum/messaging',
                {
                    headers: {
                        authorization: token,
                    },
                }
            )
            if (name.data) {
                window.localStorage.setItem('messages', name)
            }
        } catch {
            console.log('failed')
        }
    })
    const messages = window.localStorage.getItem('messages')

    const Submit = (e) => {
        e.preventDefault()
        axios
            .post(
                'https://jobs-xmmtw.ondigitalocean.app/Forum/messaging',
                {
                    content: message,
                    name: name,
                    date: Date.now(),
                },
                {
                    headers: {
                        authorization: token,
                    },
                },
                {}
            )
            .then((res) => {})
            .catch((err) => {
                alert(err)
            })
    }
    const Change = (e) => {
        setMessage(e.target.value)
    }

    console.log(messages)

    return (
        <div className="messageCont">
            <div className="Messages">
                <form onSubmit={Submit}>
                    <input onChange={Change} value={message} />
                    <button onClick={Submit}>Send</button>
                </form>
            </div>
        </div>
    )
}

export default Forum
