import React from 'react'
import {useState , useEffect,useReducer} from 'react'
import axios from 'axios'
import legend from '../images/legend.png'
import pro from '../images/Pro.png'
import superstar from '../images/superstar.png'
import Elite from '../images/Elite.png'
import allstar from '../images/allstar.png'
import Search from './Search'
import {connect} from 'react-redux'
import {axiosWithAuth} from './axosWithAuth'
import {getId} from '../actions/postActions'
const PlayerCard = (props) => {

useEffect(()=>{
    props.getId();

},[])




    const[playerInfo,setPlayerInfo] = useState([])
        const[classs,setClasss] = useState('blurry')
        const[searchQuery,setSearchQuery] = useState()
                const[searchQuery2,setSearchQuery2] = useState()

const token = window.localStorage.getItem('token')

useEffect(()=>{
    axios.get('http://localhost:3000/player',{
        headers: {
            authorization: token
        }
    })
    .then(res=>{
        setPlayerInfo(res.data)
    })
},[props.One._id])

    const fix = () =>{
        setClasss('default')  
    }
const j = playerInfo.filter(item=>
item.Archetype.toLowerCase().includes(searchQuery) && item.Overall.toLowerCase().includes(searchQuery2)


)
// console.log(j)
const Change = (e) =>{
setSearchQuery(e.target.value.toLowerCase())
}

const Change2 = (e) =>{
setSearchQuery2(e.target.value.toLowerCase())
}

   const CredOff = {
    Status:'offline'
   }
const Logout =  () =>{
  axiosWithAuth().patch(`/player/{ _id: ${props.One._id}, __v: 0 }`,CredOff)
  .then(window.localStorage.setItem("token",''))  

}

    return (
        <div>
             <nav>
                        <a href = '/login' onClick={Logout}> Logout </a>

             </nav>




          {/* <Search/> */}
        <div id={classs} className ='cont'>
             {/* <input placeholder ='Archetyper' onChange = {Change} value = {searchQuery}/>
                          <input placeholder ='Overall' onChange = {Change2} value = {searchQuery2}/>
 */}

            {playerInfo.map(info=>{  
                return (
                    <div className = 'infoCont'>
                        <div className = {info.System} >
                            <p className = 'system'> {info.System} </p>
                            </div>
                            <div className = {info.Status}>
                            </div>
                     <img className = 'legend' src = {info.Rep} />
                    <p> Username</p>
                     <p> Archetype {info.Archetype}</p>
                     <p>OVR{info.Overall}</p>
                     <p> Position {info.Position}</p>
                     <p> Win percentage {info.Winpercentage}</p>
                     <p> Play Style {info.Type}</p>
                     <p>{j.Archetype}</p>
                        </div>
                )
            })}
            
        </div>
         </div>
    )
}

const mapStateToProps =  state=>({
    One:state.getId.UserId
})

export default connect(mapStateToProps,{getId})(PlayerCard)
