import React from 'react'
import { useState,useEffect } from 'react'
import axios from 'axios'
import CreatePlayer from './CreatePlayer'
import {connect} from 'react-redux'

import {getId} from '../actions/postActions'
import { Redirect } from 'react-router'
const MyPlayer = (props) => {
let id = props.match.params.id
const token = window.localStorage.getItem('token')
const [myPlayer,setMyPlayer]=useState([])
const [playerInfo,setPlayerInfo] =useState({
    Archetype:"",
    Overall:"",
    Winpercentage:"",
    Rep:"",
    Type:"",
    System:"",
    Position:""

})
            //   "http://localhost:3000/player/{ _id: 5fd9811cc0cd184690c65f07, __v: 0 }"
   useEffect(()=>{
            props.getId()

   })
   useEffect(()=>{
       
    axios.get(`http://localhost:3000/player/{ _id: ${props.match.params.id}, __v: 0 }`,{
        headers: {
            authorization: token
        }
    })
    .then(res=>{
    setMyPlayer(res.data)
    })
    .catch(err=>{
            })


   },[])
   

   const onSubmit = () =>{

   }

   const onChange = (e) =>{
       let name = e.target.value
       if(name.length < 3){
       setPlayerInfo(playerInfo=>({
           ...playerInfo,
           [e.target.name]:e.target.value

       }))
    }
                console.log(playerInfo)

    }
   const onChange2 = (e) =>{
       let name = e.target.value
       setPlayerInfo(playerInfo=>({
           ...playerInfo,
           [e.target.name]:e.target.value

       }))
    
                console.log(playerInfo)

    }

    const SelectChange = (e)=>{
               setPlayerInfo(playerInfo=>({
           ...playerInfo,
           [e.target.name]:e.target[e.target.selectedIndex].value

       }))

       console.log(playerInfo)

    }

const Form = () =>{
    if(myPlayer !== null){
               return(
            <div className = 'mCont'>
            <div className = 'myPlayerCont'>
            <div className = {myPlayer.System} >
                            <p className = 'system'> {myPlayer.System} </p>
                            </div>
                            <div className = {myPlayer.Status}>
                            </div>
                     <img className = 'legend' src = {myPlayer.Rep} />
                    <p> Username</p>
                     <p> Archetype {myPlayer.Archetpye}</p>
                     <p>OVR{myPlayer.Overall}</p>
                     <p> Position {myPlayer.Position}</p>
                     <p> Win percentage {myPlayer.Winpercentage}</p>
                     <p> Play Style {myPlayer.Type}</p>
            </div>
            </div>
        )
            
      }
      else{
     return(
            <div className = 'mCont'>
            <form> 
             <div className = 'selects'>
             <select name = 'Rep' onChange={SelectChange}>
             <option > Pro </option>     
             <option> Allstar </option>
             <option> Superstar </option>
             <option> elite </option>     
             <option> Legend </option>
             </select>     
             <select name = 'Type'  onChange={SelectChange}>
             <option> Casual </option>     
             <option> Competetive </option>
             </select>
             <select name = 'System'  onChange={SelectChange}>
             <option> Playstation </option>     
             <option> Xbox </option>
             </select>                             
             <select name = 'Position' onChange={SelectChange}>
             <option> Point Gaurd </option>     
             <option> Shooting Gaurd </option>
             <option> Small Forward </option>
             <option> Power Forward </option>
             <option> Center </option>
             </select>  
               <div className = 'myPlayerCont'>
              <input value = {playerInfo.Archetype} name = 'Archetype' onChange={onChange2} placeholder = 'Archetype' />  
              <input value = {playerInfo.Overall} name ='Overall' onChange={onChange}placeholder = 'Overall' />      
              <input value = {playerInfo.Winpercentage} name = 'Winpercentage' onChange={onChange}placeholder = 'Winpercentage' /> 
              <button>Submit </button>     
             </div>
              </div>
              </form> 

             </div>


          )
      }
}




    return Form()
        
    
}
const mapStateToProps =  state=>({
    One:state.getId.UserId
})

export default connect(mapStateToProps,{getId})(MyPlayer)
