import {POST_ID,GET_ID} from './types'
import axios from 'axios'
 const token = window.localStorage.getItem("token");
export const getId = () => dispatch =>{
  useEffect(() => {
        if(token!== ''){
        axios.get("http://localhost:3000/login", {
            headers: {
              authorization: token,
            },
          })
          .then(UserId=> {
            dispatch({type:GET_ID,
            payload:UserId.data
            })
          })};
  })
    }
