import React from 'react'
import { useState, useEffect } from 'react'
import axios from 'axios'

const ForgotPassword = () => {
    const [emails, setEmails] = useState('barreahmed600@gmail.com')
    const [view, setView] = useState()
    const [error, setError] = useState()
    const [submitted, setSubmitted] = useState()

    const Change = (e) => {
        setEmails(e.target.value)
    }

    const Submit = (e) => {
        e.preventDefault()
        axios
            .post('http://localhost:3000/login/forgot', { email: emails })
            .then((res) => {
                setView('none')
                setSubmitted(
                    <div className="forgotConts">
                        <div className="forgotCont">
                            <div className="Reserved reset">
                                <h1> Link sent </h1>
                                <p className="p2">
                                    Check your email for a reset Link
                                </p>
                            </div>
                        </div>
                    </div>
                )
            })
            .catch((err) => {
                setError(
                    <div className="error">
                        This email isn't associated with your account
                    </div>
                )
            })
    }
    return (
        <div className="forgot">
            {submitted}
            <form className={view} onSubmit={Submit}>
                {error}
                <p>Enter your email for a reset link </p>
                <input
                    onChange={Change}
                    type="email"
                    value={emails}
                    placeholder="Enter your email"
                />
                <button onClick={Submit}> Send </button>
            </form>
        </div>
    )
}

export default ForgotPassword
