import { POST_ID, GET_ID, GETMY_ID } from '../actions/types'
import axios from 'axios'
const token = window.localStorage.getItem('token')
const initialState = {
    UserId: [],
    UserInfo: [],
}
axios
    .get(
        `https://jobs-xmmtw.ondigitalocean.app/player/{ _id: ${initialState.UserId._id}, __v: 0 }`,
        {
            headers: {
                authorization: token,
            },
        }
    )
    .then((res) => {
        UserInfo = res.data
    })

export default function (state = initialState, action) {
    switch (action.type) {
        case GET_ID:
            return {
                ...state,
                UserId: action.payload,
            }

        case GETMY_ID:
            return {
                ...state,
                UserInfo: action.payload,
            }
        default:
            return state
    }
}
