import React from "react";
import { axiosWithAuth } from "../Components/axiosWithAuth";
import { Link, Redirect, useHistory } from "react-router-dom";
import axios from "axios";
import { useState, useEffect, useReducer, withRouter } from "react";

const Signup = (props) => {
  const [state, setState] = useState({
    name:"",
    email: "",
    password: ""
  });
  

  const [submitted, setSubmitted] = useState();

  let history = useHistory();
  const token = window.localStorage.getItem("token");
  if (token !== "") {
    return <Redirect to="/" />;
  }



  const handleChange = (e) => {
    const { id, value } = e.target;
    setState((state) => ({
      ...state,
      [id]: value,
    }));
  };
  console.log(state);
  const[reserved,setReserved] = useState(
<div className="auth-cont">
          
                     <form className="Login" onSubmit={handleSubmit}>
                   {/* <h4> Welcome Welcome Create A Team Finder Account</h4> */}
                     <div className= 'Reserved'>
            <p> {reserved}</p>
          </div>
                   <h4> Welcome Reserve your Team Finder Account</h4>

                  <input
                id="name"
                placeholder="name"
                value={state.name}
                onChange={handleChange}
              />
              <input
                id="email"
                placeholder="email"
                value={state.email}
                onChange={handleChange}
              />
              <input
                type="password"
                value={state.password}
                id="password"
                placeholder="Password"
                onChange={handleChange}
              />
                <a className = 'authSubmit' onClick={handleSubmit}>
                Signup
              </a>

                {/* <Link className = 'authLinks' to = '/login'> Have an account login</Link> */}
            </form>
          </div>



  )
  const handleSubmit = (e) => {
    e.preventDefault();
    axiosWithAuth()
      .post("/signup", state)
      .then((res) => {
        // window.localStorage.setItem("token", res.data.token);
        // window.location.reload();
        console.log(res)
        setReserved('Your account has been reserved well email you when team finder is released.')
      })
      .catch((err)=>{
       console.log(err)
      })
  };
  return (
  <div className='login-cont'>
          

        {reserved}
        </div>
  );
};

export default Signup;
