import React from "react"
import { axiosWithAuth } from "../Components/axosWithAuth";
import {Link} from "react-router-dom"

class Signup extends React.Component {
    constructor() {
        super();
        this.state = {
            credentials: {
                name:'',
                email: '',
                password: ''
            },
            alert:"",

        }
        const token = window.localStorage.getItem('token')
    }

    componentDidMount(){
        axiosWithAuth().get("/Signup")
        .then(res => {
        //   this.props.history.push('/find')
        })
        .catch(()=>{
        })
      
    }

    handleChange = e => {
   		this.setState({credentials: {
            ...this.state.credentials,
            [e.target.name]: e.target.value
        }});
    }

    handleSubmit = e => {
        e.preventDefault();
        
        axiosWithAuth().post('/Signup', this.state.credentials)
        .then(res => {
            window.localStorage.setItem('token', res.data.token);
            this.props.history.push('/find');

        })
        .catch(err => {
            this.setState({...this.state,alert:"Unable to sign in"});
        })

    
    }   

    render() {
        return (
            <div> 
                  <span className = "logos"><img className = "logo-img" /> </span>
                  <div className = "auth-container" >

            <div className = "auth-cont" >
            <h1 className = "welcome"> We are <span> Ts Jobs</span></h1>
<h4> Welcome  login!</h4>

                                <p className = "alert"> {this.state.alert} </p>
            <div className="LoginPage">
                <form className = "Login" onSubmit={this.handleSubmit}>
                    <input name="name" placeholder="name" onChange={this.handleChange}/>
                    <input name="email" placeholder="email" onChange={this.handleChange}/>
                    <input type = "password"name="password" placeholder="Password" onChange={this.handleChange}/>
                    </form>

                   <div className = "auth">
                   <button onClick ={()=> this.props.history.push('/Login')}  className = "signup-button">
                       
                   <Link>Login</Link>
                       </button>

                   <button onClick = {this.handleSubmit} className = "login-button" >Sign up</button>
                   </div>

        




            </div>
            </div>
            <div className = "login-blue-cont">
            <div className = "login-blue">

</div>
            </div>
     
            </div>
</div>
        )
    }
}

export default Signup
