import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { getId } from '../../actions/postActions'
import { connect } from 'react-redux'

import './Join.css'

const SignIn = ({ One: { name } }) => {
    const [room, setRoom] = useState('')
    const sel = document.querySelector('.sel')
    console.log(room)

    return (
        <div className="joinOuterContainer">
            <div className="joinInnerContainer">
                <h1 className="heading">Join</h1>
                <div>
                    <button
                        onClick={() => setRoom('Playstation')}
                        value="Playstation"
                    >
                        Playstation{' '}
                    </button>
                    <button onClick={() => setRoom('XBOX')} value="XBOX">
                        XBOX
                    </button>
                </div>
                <Link
                    onClick={(e) => (!room ? e.preventDefault() : null)}
                    to={`/chat?name=${name}&&room=${room}`}
                >
                    <button className={'button mt-20'} type="submit">
                        Sign In
                    </button>
                </Link>
            </div>
        </div>
    )
}
const mapStateToProps = (state) => ({
    One: state.getId.UserId,
})

export default connect(mapStateToProps, { getId })(SignIn)
