import React from 'react'
import { useState, useEffect } from 'react'
import axios from 'axios'
const Reset = (props) => {
    const [password, setpassword] = useState()
    const [alert, setAlert] = useState()
    const [view, setView] = useState()
    const [expired, setExpired] = useState()
    const [success, setSuccess] = useState()

    const Change = (e) => {
        setpassword(e.target.value)
    }

    const axiosWithAuth = () => {
        const token = props.match.params.token
        return axios.create({
            headers: {
                authorization: token,
            },
            baseURL: 'https://jobs-xmmtw.ondigitalocean.app/',
        })
    }

    useEffect((e) => {
        axiosWithAuth()
            .get('login/verify')
            .then((res) => {
                console.log(res)
                setAlert(res.data)

                setExpired(res.data)
            })
    })

    const Submit = (e) => {
        e.preventDefault()
        axios
            .post('https://jobs-xmmtw.ondigitalocean.app/login/reset', {
                password: password,
                id: props.match.params.id,
            })
            .then((res) => {
                console.log(res)
                setSuccess('Sucessfully Updated reset')
            })
    }

    console.log(props.match.params.token)
    return (
        <div className="forgot">
            <form onSubmit={Submit}>
                <p className={success}> {alert} </p>

                <p className={view}>Enter your password for a reset link </p>
                <input
                    className={view}
                    onChange={Change}
                    type="password"
                    value={password}
                    placeholder="Enter your password"
                />
                <button className={view} onClick={Submit}>
                    {' '}
                    Send{' '}
                </button>
            </form>
        </div>
    )
}

export default Reset
