import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { getId } from '../../../actions/postActions'
import { connect } from 'react-redux'
import './Message.css'

import ReactEmoji from 'react-emoji'

const Message = ({ One: { name, _id }, message: { text, user } }) => {
    const [myname, setMyname] = useState()
    const token = window.localStorage.getItem('token')
    useEffect(() => {}, [])
    let isSentByCurrentUser = false
    if (user === name) {
        isSentByCurrentUser = true
    }

    return isSentByCurrentUser ? (
        <div className="messageContainer justifyEnd">
            <div className="messageBox backgroundBlue">
                <p className="messageText colorWhite">
                    {ReactEmoji.emojify(text)}
                </p>
            </div>
        </div>
    ) : (
        <div className="messageContainer justifyStart">
            <div className="messageBox backgroundLight">
                <p className="messageText colorDark">
                    {ReactEmoji.emojify(text)}
                </p>
            </div>
            <a target="_blank" href={`/playerView/${_id}`}>
                <p className="sentText pl-10 ">{name}</p>
            </a>
        </div>
    )
}

const mapStateToProps = (state) => ({
    One: state.getId.UserId,
})

export default connect(mapStateToProps, { getId })(Message)
