import { POST_ID, GET_ID } from './types'
import axios from 'axios'
import { useEffect } from 'react'
const token = window.localStorage.getItem('token')
export const getId = () => (dispatch) => {
    if (token !== null && token !== '') {
        axios
            .get('https://jobs-xmmtw.ondigitalocean.app/login', {
                headers: {
                    authorization: token,
                },
            })
            .then((UserId) => {
                dispatch({ type: GET_ID, payload: UserId.data })
            })
            .catch((error) => {
                window.localStorage.removeItem('token')
            })

        axios
            .get(
                `https://jobs-xmmtw.ondigitalocean.app/player/{ _id: ${props.match.params.id}, __v: 0 }`,
                {
                    headers: {
                        authorization: token,
                    },
                }
            )
            .then((UserInfo) => {
                dispatch({
                    type: GET_ID,
                    payload: UserInfo.data,
                })
            })
            .catch((err) => {})
    }
}
